using UnityEngine;using System.Collections;public class AngleTest : MonoBehaviour {	public Transform _vector1,_vector2,_vector3;	public Vector2 bird {		get {			return new Vector2(_vector1.position.x,_vector1.position.y);		}	}	public Vector2 target {		get {			return new Vector2( _vector2.position.x,_vector2.position.y);			}	}	public Vector2 direction {		get {			return new Vector2( _vector3.position.x,_vector3.position.y);			}	}		void OnGUI() {		if (_vector1 != null && _vector2 != null) {			GUILayout.Label("V1: " + bird.ToString());			GUILayout.Label("V2: " + target.ToString());			GUILayout.Label("V3: " + direction.ToString());			//we position center on bird			//target - bird			//we find angle between direction and target			//GUILayout.Label("stuff:"+ Vector2.Angle(direction,target-bird));			//we check if left or right						if ((Vector2.Angle(Vector2.right,direction-bird) + Vector2.Angle(Vector2.right,target-bird)) > 180f) {				GUILayout.Label("Is left");				}			else {				GUILayout.Label("Is right");			}						/*			GUILayout.Label (Mathf.Abs(bird.x - target.x).ToString());			GUILayout.Label (Mathf.Abs(bird.y - target.y).ToString());			if ( Mathf.Abs (bird.x - target.x) < Mathf.Abs(bird.y - target.y) ) {							}			else {							}			*/								}	}}